function fix($str)
{
    return strtolower(str_replace([',', '.', '[', ']', '-', '^', '$', '(', ')', '*', '|', '{', '}', '\\', '#', '&', '!', '~', '/', ' ', '\'', '"', ':', ';'], '', $str));
}
